    private void MONEYStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_MONEYStateChanged
        if (MONEY.isSelected()){
           General.Alquileres_Registrados.montopromedio();
           String datos="";
           int index=0;
          for(String item:Categorias){
              datos+="\n"+item+"\n"
                      +"Monto Total= "+Montos.get(index)+"\n"
                      +"Promedio= "+Promedios.get(index)+"\n";
              index+=1;
          }
          RESULT.setText(datos);
       }
    }//GEN-LAST:event_MONEYStateChanged

    private void TOPCLIENTSStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_TOPCLIENTSStateChanged
       if (TOPCLIENTS.isSelected()){
            ArrayList<Nodo_Cliente> top5;
            String datos="";
            int num=1;
           top5=General.Clientes_Registrados.top5();
           for(Nodo_Cliente item:top5){
               datos+="Num " + num + "\n"+General.Clientes_Registrados.printPersona(item.getPersona());
               num+=1;
           }
           RESULT.setText(datos);
       }
    }//GEN-LAST:event_TOPCLIENTSStateChanged

    private void TOPCARSStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_TOPCARSStateChanged
        if (TOPCARS.isSelected()){
            ArrayList<Nodo_Vehiculo> top5;
            String datos="";
            int num=1;
           top5=General.Vehiculos_Registrados.Top5();
           for(Nodo_Vehiculo item:top5){
               datos+="Num " + num +"\n"+ General.Vehiculos_Registrados.buscar(item.getCarro().getPlaca());
               num+=1;
           }
           RESULT.setText(datos);
       }
    }//GEN-LAST:event_TOPCARSStateChanged

